import React, {Component} from 'react';
import Login from "./components/login_component/Login";
import Home from "./components/Home/Home"
import {BrowserRouter as Router,Route,Link,Switch} from "react-router-dom";
import axios from "axios";

class App extends Component{

    constructor(props) {
        super(props)
        this.state={
            username: [],
            password: [],
            loggedIn: false,
            token:""
        };
    }

    onUsernameChange=(event) =>{ this.setState({username:event.target.value});};

    onPasswordChange=(event) =>{ this.setState({password:event.target.value});};



    handleSubmit = (event) => {
        event.preventDefault();
        let inputData={"username":this.state.username,"password":this.state.password}
        console.log(inputData);
        axios.post("/login", inputData)
            .then(response => {
                this.setState({token: response.data});
                if(response.status===200){
                    this.setState({loggedIn:true});
                    console.log(event);
                    console.log("Successfully Logged In!");
                }
                console.log(response)
            })
    }

    render() {
      if(this.state.loggedIn){
          return (
              <div className="App">
                  <Router>
                      <div>
                          <nav>
                              <ul>
                                  <li>
                                      <Link to="/login">Login</Link>
                                  </li>
                                  <li>
                                      <Link to="/">Home</Link>
                                  </li>
                              </ul>
                          </nav>
                      </div>
                      <Switch>
                          <Route exact path="/login">
                              <Login loggedIn={this.state.loggedIn} handleSubmit={this.handleSubmit}
                                     username={this.state.username} password={this.state.password}
                                     onUsernameChange={this.onUsernameChange} onPasswordChange={this.onPasswordChange}/>
                          </Route>
                          <Route exact path="/">
                              <Home />
                          </Route>
                      </Switch>
                  </Router>

                  <Home/>
              </div>
          );
      }
      else {
          return (
              <div className="App">
                  <Router>
                      <div>
                          <nav>
                              <ul>
                                  <li>
                                      <Link to="/login">Login</Link>
                                  </li>
                                  <li>
                                      <Link to="/">Home</Link>
                                  </li>
                              </ul>
                          </nav>
                      </div>
                      <Switch>
                          <Route exact path="/login">
                              <Login loggedIn={this.state.loggedIn}/>
                          </Route>
                          <Route exact path="/">
                              <Home />
                          </Route>
                      </Switch>
                  </Router>
              </div>
          );
      }
  }
}

export default App;
