import React, {Component} from "react";
import { Button, FormGroup, FormControl} from "react-bootstrap";
import axios from "axios";

class Login extends Component{

    constructor() {
        super();
        this.state = {
            username: [],
            password: [],
            token: ""
        }
    }

    validateForm = () => {
        return this.state.username.length > 0 && this.state.password.length > 0;
    }

    handleSubmit = (event) => {
        event.preventDefault();
    }

    LoginValidation = () => {
        let inputData={"username":this.state.username,"password":this.state.password}
        console.log(JSON.stringify(inputData));
        axios.post("http://localhost:8080/login",JSON.stringify(inputData))
            .then(response => {
                this.setState({token: response.data})
                console.log(response)
            })
    }
    render() {
        return (
            <div className="Login">
                <form onSubmit={this.handleSubmit}>
                    <FormGroup controlId="username" bsSize="large">
                        <FormControl
                            autoFocus
                            type="username"
                            value={this.state.username}
                            onChange={e=> this.setState({username:e.target.value})}
                        />
                    </FormGroup>
                    <FormGroup controlId="password" bsSize="large">
                        <FormControl
                            value={this.state.password}
                            onChange={e=> this.setState({password:e.target.value})}
                            type="password"
                        />
                    </FormGroup>
                    <Button onClick={this.LoginValidation()} block bsSize="large" disabled={!this.validateForm()} type="submit">
                        Login
                    </Button>
                </form>
            </div>
        );
    }
}

export default Login;
