import React, {Component} from 'react';
import Login from "./components/login_component/Login";
import Home from "./components/Home/Home"
import {BrowserRouter as Router,Route,Link,Switch} from "react-router-dom";

class App extends Component{

    constructor(props) {
        super(props)
        this.onChangeLoggedIn=this.onChangeLoggedIn.bind(this);
        this.state={
            loggedIn: false
        };
    }



    onChangeLoggedIn=() =>{   this.setState({ loggedIn: true });}

    render() {
      if(this.state.loggedIn){
          return (
              <div className="App">
                  <Router>
                      <div>
                          <nav>
                              <ul>
                                  <li>
                                      <Link to="/login">Login</Link>
                                  </li>
                                  <li>
                                      <Link to="/">Home</Link>
                                  </li>
                              </ul>
                          </nav>
                      </div>
                      <Switch>
                          <Route exact path="/login">
                              <Login loggedIn={this.state.loggedIn} onChangeLoggedIn={this.onChangeLoggedIn}/>
                          </Route>
                          <Route exact path="/">
                              <Home />
                          </Route>
                      </Switch>
                  </Router>

                  <Home/>
              </div>
          );
      }
      else {
          return (
              <div className="App">
                  <Router>
                      <div>
                          <nav>
                              <ul>
                                  <li>
                                      <Link to="/login">Login</Link>
                                  </li>
                                  <li>
                                      <Link to="/">Home</Link>
                                  </li>
                              </ul>
                          </nav>
                      </div>
                      <Switch>
                          <Route exact path="/login">
                              <Login loggedIn={this.state.loggedIn}/>
                          </Route>
                          <Route exact path="/">
                              <Home />
                          </Route>
                      </Switch>
                  </Router>
              </div>
          );
      }
  }
}

export default App;
