import React, {Component} from 'react';
import Login from "./components/login_component/Login";
import Home from "./components/Home/Home"
import {BrowserRouter as Router,Route,Link,Switch} from "react-router-dom";
import OutsideUserHome from "./components/Home/OutsideUserHome";
import Button from "@material-ui/core/Button";
import Charts from "./components/List/Charts";
import ShowOnMap from "./components/map_components/ShowOnMap";

class App extends Component{

    constructor(props) {
        super(props)
        this.state={
            username: [],
            password: [],
            loggedIn: false
        };
    }



    onChangeLoggedIn=(username,password,token) =>{
        if(token===""){
            console.log("Bad Credentials!");
        }
        else{
            console.log(this.state);
            this.setState({ username: username, password: password, loggedIn: true });
            console.log(this.state);
        }
    }

    onChangeLoggedOut=() =>{
        console.log(this.state);
        this.setState({ username: "", password: "", loggedIn: false });
        console.log(this.state);
    }

    render() {
      if(this.state.loggedIn){
          console.log(this.state)
          return (
              <div className="App">
                  <Router>
                      <div>
                          <nav>
                              <ul>
                                  <li>
                                      <Link to="/">Home</Link>
                                  </li>
                                  <li>
                                      <Link to="/charts">Charts</Link>
                                  </li>
                                  <li>
                                      <Link to="/map">Map</Link>
                                  </li>
                              </ul>
                          </nav>
                      </div>
                      <Switch>
                          <Route exact path="/">
                              <div>
                                  <Button variant="contained"
                                          color="primary"
                                          style={{float: "left"}}
                                          onClick={this.onChangeLoggedOut}>
                                      Logout
                                  </Button>
                                  <Home username={this.state.username}/>

                              </div>
                          </Route>
                          <Route exact path="/charts">
                              <Charts/>
                          </Route>
                          <Route exact path="/map">
                              <ShowOnMap/>
                          </Route>
                      </Switch>
                  </Router>

                  <Home/>
              </div>
          );
      }
      else {
          return (
              <div className="App">
                  <Router>
                      <div>
                          <nav>
                              <ul>
                                  <li>
                                      <Link to="/login">Login</Link>
                                  </li>
                                  <li>
                                      <Link to="/">Home</Link>
                                  </li>
                              </ul>
                          </nav>
                      </div>
                      <Switch>
                          <Route exact path="/login">
                              <Login onChangeLoggedIn={this.onChangeLoggedIn}/>
                          </Route>
                          <Route exact path="/">
                              <OutsideUserHome/>
                          </Route>
                      </Switch>
                  </Router>
              </div>
          );
      }
  }
}

export default App;
