import React, { Component } from 'react'
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import {
    Chart,
    BarSeries,
    Title,
    ArgumentAxis,
    ValueAxis,
} from '@devexpress/dx-react-chart-material-ui';

import { Animation } from '@devexpress/dx-react-chart';

export default class UserBarChart extends Component {
    constructor(props) {
        super(props);
        this.state = { Data: {} };
    }
    componentDidMount=()=> {
        axios.get("/users/activities")
            .then(res => {
                console.log(res);
                const ipl = res.data;
                let playername = [];
                let runscore = [];
                ipl.forEach(record => {
                    playername.push(record.activityName);
                    runscore.push(record.registered);
                });
                this.setState({
                    Data: {
                        labels: playername,
                        datasets: [
                            {
                                label: 'Registered',
                                data: runscore,
                            }
                        ]
                    }
                });
            })
    }
    render() {
        return (
            <div>
                <Paper>
                    <Chart
                        data={this.state.Data}
                    >
                        <ArgumentAxis />
                        <ValueAxis />

                        <BarSeries
                            valueField="Registered"
                            argumentField="Activity Name"
                        />
                        <Title text="Registered" />
                        <Animation />
                    </Chart>
                </Paper>
            </div>
    )
    }
}
