import React, {Component} from 'react';
import PaginationTable from "./components/table/PaginationTable";
import Button from "@material-ui/core/Button";
import PlusIcon from '@material-ui/icons/Add';
import axios from "axios";
import Snackbar from "@material-ui/core/Snackbar";
import Alert from "@material-ui/lab/Alert";
import ReactDialog from "./components/common/ReactDialog";

class App extends Component{
  activityDialogFields = [
    {id: "activityName", label: "Activity Name", type: "text"},
    {id: "startDate", label: "Start Date", type: "text"},
    {id: "endDate", label: "End Date", type: "text"},
  ]


  constructor() {
    super();
    this.state = {
      rows: [],
      addActivityModalOpen: false,
      snackbarProperties: {
        isOpen: false,
        message: "",
        severity: ""
      }
    }
  }

  componentDidMount() {
    axios.get("/users/admin/activities")
        .then(response => {
          this.setState({rows: response.data})
        })
  }

  toggleAddActivityModal = () => {
    this.setState({addActivityModalOpen: !this.state.addActivityModalOpen})
  }

  submitActivityAdd = (inputData) => {
    this.toggleAddActivityModal();
    axios.post("/users/admin/activities", inputData)
        .then(response => {
          this.setState(prevState => (
              {rows: [...prevState.rows, response.data]}
          ));
          this.snackbarOpen("Activity has been added successfully!", "success");
        })
        .catch(error => {
          if (error.response.status === 400) {
            this.snackbarOpen(error.response.data.errors[0].defaultMessage, "error")
          }
          console.log(error.response);
        })
  }

  snackbarOpen = (message, severity) => {
    console.log(message, severity);
    this.setState(prevState => {
      let snackbarProperties = {...prevState.snackbarProperties}
      snackbarProperties.isOpen = true;
      snackbarProperties.message = message;
      snackbarProperties.severity = severity;
      return {snackbarProperties};
    })
  }

  snackbarClose = () => {
    this.setState(prevState => {
      let snackbarProperties = {...prevState.snackbarProperties}
      snackbarProperties.isOpen = false;
      snackbarProperties.message = "";
      snackbarProperties.severity = "";
      return {snackbarProperties};
    })
  }

  onActivityUpdate = (inputData) => {
    axios.put("/users/admin/activities/" + inputData.activityName, inputData)
        .then(response => {
          this.setState( {
            rows: this.state.rows.filter((activity) => activity.name !== inputData.activityName)
          })
            this.setState(prevState => (
                {rows: [...prevState.rows, response.data]}
            ));
          this.snackbarOpen("Activity: " + inputData.activityName + " has been updated!", "success")
        })
  }

  onActivityDelete = (activityName) => {
    axios.delete("/users/admin/activities/" + activityName)
        .then(response => {
          this.setState( {
            rows: this.state.rows.filter((activity) => activity.activityName !== activityName)
          })
          this.snackbarOpen("Activity: " + activityName + " has been deleted!", "success")
        })
  }

  render() {

    return (
        <div className="App">
          <Button variant="contained"
                  color="primary"
                  style={{float: "right"}}
                  onClick={this.toggleAddActivityModal}
                  startIcon={<PlusIcon/>}>
            Add Activity
          </Button>
          <Snackbar open={this.state.snackbarProperties.isOpen} autoHideDuration={5000} onClose={this.snackbarClose}
                    anchorOrigin={{vertical: 'top', horizontal: 'right'}}>
            <Alert onClose={this.snackbarClose} severity={this.state.snackbarProperties.severity}>
              {this.state.snackbarProperties.message}
            </Alert>
          </Snackbar>
          <ReactDialog fields={this.activityDialogFields} title="Add Activity" isOpen={this.state.addActivityModalOpen} onClose={this.toggleAddActivityModal}
                       onSubmit={this.submitActivityAdd}/>
          <PaginationTable rows={this.state.rows} onUpdate={<ReactDialog fields={this.activityDialogFields} title="Update Activity" isOpen={this.state.addActivityModalOpen} onClose={this.toggleAddActivityModal}
                                                                         onSubmit={this.onActivityUpdate}/>} onDelete={this.onActivityDelete}/>
        </div>
    );
  }
}

export default App;
