import React, {Component} from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import axios from "axios";

export class SimpleMap extends Component {
    constructor() {
        super();
        this.state = {
            selectedPlace:[],
            bounds:[]
        }
    }

    componentDidMount() {
        axios.get("/users/"+this.props.username+"/activities")
            .then(response => {
                this.setState(prevState => (
                    {selectedPlace: [...prevState.selectedPlace, response.data]}
                ));
                console.log(this.state.selectedPlace)
                let bounds = new this.props.google.maps.LatLngBounds();
                for (var i = 0; i < this.state.selectedPlace[0].length; i++) {
                    bounds.extend(this.state.selectedPlace[0][i]);
                }
            })
    }

    render() {
        return (
            <Map google={this.props.google} zoom={14}>

                <Marker onClick={this.onMarkerClick}
                        name={'Current location'} />

                <InfoWindow onClose={this.onInfoWindowClose}>
                    <div>
                        <h1>{this.selectedPlace}</h1>
                    </div>
                </InfoWindow>
            </Map>
        );
    }
}

export default GoogleApiWrapper({
    apiKey: ("AIzaSyCZnNLO-rWxVk_q-vImQbbduYCaAX7HtI8")
})(SimpleMap)
