import React, {Component} from 'react';
import Login from "./components/login_component/Login";
import Home from "./components/Home/Home"
import {BrowserRouter as Router,Route,Link,Switch} from "react-router-dom";
import axios from "axios";

class App extends Component{

    constructor(props) {
        super(props)
        this.state={
            loggedIn: false,
            token:""
        };
    }

    onChange=() =>{ this.setState({loggedIn:true});};



    handleSubmit = (event) => {
        event.preventDefault();
        let inputData={"username":this.state.username,"password":this.state.password}
        console.log(inputData);
        axios.post("/login", inputData)
            .then(response => {
                this.setState({token: response.data});
                if(response.status===200){
                    this.setState({logIn:this.props.onChangeLoggedIn});
                    console.log(event);
                    console.log(this.state.logIn);
                    console.log(this.props.loggedIn);
                    console.log("Successfully Logged In!");
                }
                console.log(response)
            })
    }

    render() {
      if(this.state.loggedIn){
          return (
              <div className="App">
                  <Router>
                      <div>
                          <nav>
                              <ul>
                                  <li>
                                      <Link to="/login">Login</Link>
                                  </li>
                                  <li>
                                      <Link to="/">Home</Link>
                                  </li>
                              </ul>
                          </nav>
                      </div>
                      <Switch>
                          <Route exact path="/login">
                              <Login loggedIn={this.state.loggedIn} handleSubmit={this.handleSubmit}/>
                          </Route>
                          <Route exact path="/">
                              <Home />
                          </Route>
                      </Switch>
                  </Router>

                  <Home/>
              </div>
          );
      }
      else {
          return (
              <div className="App">
                  <Router>
                      <div>
                          <nav>
                              <ul>
                                  <li>
                                      <Link to="/login">Login</Link>
                                  </li>
                                  <li>
                                      <Link to="/">Home</Link>
                                  </li>
                              </ul>
                          </nav>
                      </div>
                      <Switch>
                          <Route exact path="/login">
                              <Login loggedIn={this.state.loggedIn}/>
                          </Route>
                          <Route exact path="/">
                              <Home />
                          </Route>
                      </Switch>
                  </Router>
              </div>
          );
      }
  }
}

export default App;
